import React, { useState, useEffect } from 'react'
import ReactDOM from 'react-dom'
import './index.css'
import Table from './Table'
import AddMemoForm from './AddMemoForm'

const Index = () => {
  const [category, setCategory] = useState('Things to buy')
  const [content, setContent] = useState('milk, egg')

  const memoData = [{ id: 1, category: 'todo', content: 'hair cut' }, { id: 2, category: 'meeting', content: 'with tom' }]

  const [memo, setMemo] = useState(memoData)

  const addMemo = memo => {
    memo.id = memo.length + 1
    setMemo([...memo, memo])
  }

  return (
    <div>
      <h1>CRUD app with React Hooks</h1>

      <h2 className="header">Add memo</h2>
      <AddMemoForm addMemo={addMemo} />

      <h2 className="header">Memo table</h2>
      <Table
        memo={memo}
        //  category={category} content={content}
      />
    </div>
  )
}

ReactDOM.render(<Index />, document.getElementById('root'))
